  let cart = {};
  for(let index = 0; index<all_product.length; index++){
    cart[index] = 0;
  }
  return cart;
}

const ShopContextProvider = (props) => {
    const [cartItems,setCartItems] = useState(getDefaultCart())
    const contextValue = {all_product,cartItems};

